INSERT INTO `emotion_organization` VALUES ('00103', '美国中央情报局', 'Central Intelligence Agency', '主要任务是公开和秘密地收集和分析关于国外政府、公司、恐怖组织、个人、政治、文化、科技等方面的情报，协调其它国内情报机构的活动，并把这些情报报告到美国政府各个部门的工作。', '', null, '001');
