INSERT INTO `emotion_organization` VALUES ('00107', '美国国家科学院', 'National Academy of Sciences，United States', '，是由美国著名科学家组成的组织，其成员在任期内无偿地作为“全国科学、工程和医药的顾问”，是美国科学界荣誉性及政府咨询机构。', '', null, '001');
