INSERT INTO `emotion_organization` VALUES ('00201', '中华人民共和国主席', 'President', '国家元首', '', null, '002');
INSERT INTO `emotion_organization` VALUES ('00202', '中国中央委员会', 'Central Committee of China', '是中国共产党全国代表大会产生的中共核心权力机构', '', null, '002');
INSERT INTO `emotion_organization` VALUES ('00203', '中共中央军事委员会', 'Military Commission of the Central Committee of the Communist Party of China', '中国共产党中央军事委员会是中国共产党领导下的最高军事领导机构，简称（中共）中央军委。其主要职能是：直接领导全国武装力量。其组成人员由中国共产党中央委员会决定。', '', null, '002');
INSERT INTO `emotion_organization` VALUES ('00204', '中国国务院', 'State Council of the People\'s Republic of China', '中华人民共和国国务院，即中央人民政府，是最高国家权力机关的执行机关，是最高国家行政机关。', '', null, '002');
INSERT INTO `emotion_organization` VALUES ('00205', '中华人民共和国副主席', 'Vice President of the People\'s Republic of China', '国家副主席协助中华人民共和国主席工作；受主席的委托，可以代行主席的部分职权；在主席缺位的时候，由副主席继任主席的职位；副主席缺位的时候，由全国人民代表大会进行补选。', '', null, '002');
INSERT INTO `emotion_organization` VALUES ('00206', '中华人民共和国全国人大常委会', 'the Standing Committee of the National People\'s Congress', '全国人民代表大会常务委员会简称“全国人大常委会”，是中华人民共和国最高国家权力机关——全国人民代表大会的常设机构，行使国家立法权。', '', null, '002');
