INSERT INTO `emotion_organization` VALUES ('00208', '中央纪律检查委员会', 'the Central Commission for Discipline Inspection', '中国共产党中央纪律检查委员会（简称中共中央纪委、中央纪委或中纪委）为中国共产党中央委员会的检查监督机关，担负维护党的章程和其他党内法规，检查党的路线、方针、政策和决议的执行情况，协助党的委员会加强党风建设和组织协调反腐败工作的主要任务，实行书记负责制。', '', null, '002');
